import { useRef } from 'react';
import GumletImage from './GumletImage';

/*
  ── WORK PROJECTS ────────────────────────────────────────────────
  img:   filename of the image uploaded to Gumlet (poster/thumbnail)
  video: path to hover-preview clip in public/videos/
  Replace img filenames with your actual Gumlet-uploaded image names.
  Recommended size: 1200×700px landscape
*/
const projects = [
  { tag: 'Film · 2025',        name: 'Shattered Horizons', img: 'work-shattered-horizons.jpg', video: '/videos/work-01.mp4' },
  { tag: 'Music Video · 2024', name: 'Neon Frequency',     img: 'work-neon-frequency.jpg',     video: '/videos/work-02.mp4' },
  { tag: 'Commercial · 2024',  name: 'Brand Forward',      img: 'work-brand-forward.jpg',      video: '/videos/work-03.mp4' },
  { tag: 'Documentary · 2024', name: 'Undercurrent',       img: 'work-undercurrent.jpg',       video: '/videos/work-04.mp4' },
  { tag: 'Motion · 2023',      name: 'Kinetic Forms',      img: 'work-kinetic-forms.jpg',      video: '/videos/work-05.mp4' },
  { tag: 'Photography · 2023', name: 'Still Waters',       img: 'work-still-waters.jpg',       video: '/videos/work-06.mp4' },
];

function WorkCard({ project, index }) {
  const videoRef = useRef(null);

  const handleMouseEnter = () => {
    if (videoRef.current) {
      videoRef.current.currentTime = 0;
      videoRef.current.play().catch(() => {});
    }
  };
  const handleMouseLeave = () => {
    if (videoRef.current) videoRef.current.pause();
  };

  return (
    <div
      className={`work-card reveal reveal-delay-${(index % 4) + 1}`}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
    >
      {/* Gumlet image as the always-visible background */}
      <GumletImage
        src={project.img}
        alt={project.name}
        width={1200}
        style={{ position: 'absolute', inset: 0, width: '100%', height: '100%', objectFit: 'cover', zIndex: 0 }}
      />

      {/* Gradient tint on top of image (fallback colour also defined in CSS) */}
      <div className="work-bg" />

      {/* Video preview replaces image on hover */}
      <video
        ref={videoRef}
        className="work-video"
        muted loop playsInline
        preload="none"
        src={project.video}
      />

      <div className="work-overlay">
        <div className="work-tag">{project.tag}</div>
        <div className="work-name">{project.name}</div>
      </div>
    </div>
  );
}

export default function Work() {
  return (
    <section id="work" className="work-section">
      <div className="section-label reveal">Selected Work</div>
      <h2 className="reveal reveal-delay-1">Our Work <span>Speaks</span></h2>
      <div className="work-grid">
        {projects.map((p, i) => <WorkCard key={i} project={p} index={i} />)}
      </div>
    </section>
  );
}
