import { useEffect } from 'react';
import './styles/global.css';
import './styles/sections.css';

import Intro     from './components/Intro';
import Cursor    from './components/Cursor';
import Navbar    from './components/Navbar';
import Hero      from './components/Hero';
import Marquee   from './components/Marquee';
import About     from './components/About';
import Stats     from './components/Stats';
import Services  from './components/Services';
import Work      from './components/Work';
import Showreel  from './components/Showreel';
import Team      from './components/Team';
import Contact   from './components/Contact';
import Footer    from './components/Footer';
import useScrollReveal from './components/useScrollReveal';
import { GUMLET_BASE_URL } from './config/gumlet';

export default function App() {
  useScrollReveal();

  // ── Load Gumlet.js once for lazy-load & responsive image handling ──
  useEffect(() => {
    if (document.querySelector('script#gumlet-sdk-script')) return;

    // Extract the hostname from the Gumlet base URL for config
    const gumletHost = GUMLET_BASE_URL.replace('https://', '').replace('http://', '');

    window.GUMLET_CONFIG = {
      hosts: [{
        // 'current' = your own domain when live; localhost for dev
        current: window.location.hostname,
        gumlet:  gumletHost,
      }],
      lazy_load: true,
      auto_webp: true,
    };

    const script = document.createElement('script');
    script.src = 'https://cdn.jsdelivr.net/npm/gumlet.js@2.0/dist/gumlet.min.js';
    script.id  = 'gumlet-sdk-script';
    script.async = true;
    document.body.appendChild(script);
  }, []);

  return (
    <>
      <Intro />
      <Cursor />
      <Navbar />
      <Hero />
      <Marquee />
      <About />
      <Stats />
      <Services />
      <Work />
      <Showreel />
      <Team />
      <Contact />
      <Footer />
    </>
  );
}
