import GumletImage from './GumletImage';

export default function About() {
  return (
    <section id="about" className="about-section">
      <div className="about-grid">
        <div className="about-left">
          <div className="section-label reveal">About</div>
          <h2 className="reveal reveal-delay-1">We Are <em>Built</em><br />Different</h2>
          <p className="reveal reveal-delay-2">
            AHL Productions is a multi-discipline production house rooted in the belief that great stories demand great craft. We bring together film directors, music producers, brand strategists, and digital artists under one roof.
          </p>
          <p className="reveal reveal-delay-3">
            Based in Mumbai. Working everywhere. Obsessed with quality and fueled by creative risk.
          </p>
        </div>

        {/*
          ── ABOUT IMAGE / VIDEO PANELS ───────────────────────────────
          Each panel shows a GumletImage as the poster/fallback.
          Upload your images to Gumlet and replace the src filenames.
          The video (about-01.mp4 etc.) plays on top when available.

          Recommended image sizes:
            Panel 1 (tall): 600×900px
            Panel 2 & 3 (short): 600×440px
        */}
        <div className="about-images reveal reveal-delay-2">
          {[
            { img: 'about-direction.jpg', vid: '/videos/about-01.mp4', label: 'Direction', w: 600, h: 900 },
            { img: 'about-production.jpg', vid: '/videos/about-02.mp4', label: 'Production', w: 600, h: 440 },
            { img: 'about-post.jpg', vid: '/videos/about-03.mp4', label: 'Post', w: 600, h: 440 },
          ].map((panel, i) => (
            <div className="about-img" key={i}>
              <GumletImage
                src={panel.img}
                alt={panel.label}
                width={panel.w}
                height={panel.h}
                style={{ position: 'absolute', inset: 0, width: '100%', height: '100%', objectFit: 'cover', zIndex: 0 }}
              />
              <video
                className="about-video"
                autoPlay muted loop playsInline
                src={panel.vid}
              />
              <div className="about-img-label">{panel.label}</div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}
