/**
 * GumletImage — Drop-in image component for AHL Productions
 *
 * HOW TO SET UP GUMLET:
 * 1. Sign up at https://www.gumlet.com
 * 2. Create an "Image Source" pointing to your image storage (S3, URL, etc.)
 * 3. You'll get a subdomain like: yourname.gumlet.io
 * 4. Open src/config/gumlet.js and replace GUMLET_BASE_URL with your subdomain URL
 *
 * HOW TO USE:
 *   <GumletImage
 *     src="about-direction.jpg"   ← just the filename, no domain needed
 *     alt="Direction"
 *     width={800}
 *     className="about-video"
 *   />
 *
 * Gumlet automatically:
 *   - Resizes to the right width for each device
 *   - Converts to WebP/AVIF where supported
 *   - Lazy loads via IntersectionObserver
 *   - Serves from CDN edge nodes globally
 */

import { GUMLET_BASE_URL } from '../config/gumlet';

export default function GumletImage({
  src,
  alt = '',
  width,
  height,
  className = '',
  style = {},
  fit = 'cover',       // cover | contain | fill
  quality = 80,        // 1–100
  format = 'auto',     // auto | webp | avif | jpeg | png
  lazy = true,
  ...rest
}) {
  // Build the Gumlet URL with transform params
  const buildUrl = (w) => {
    const params = new URLSearchParams();
    if (w)       params.set('w', w);
    if (height)  params.set('h', height);
    params.set('fit',     fit);
    params.set('q',       quality);
    params.set('format',  format);
    return `${GUMLET_BASE_URL}/${src}?${params.toString()}`;
  };

  // Responsive srcSet: 1x, 1.5x, 2x
  const srcSet = width
    ? `${buildUrl(width)} 1x, ${buildUrl(Math.round(width * 1.5))} 1.5x, ${buildUrl(width * 2)} 2x`
    : undefined;

  const imgSrc = buildUrl(width);

  return (
    <img
      // Gumlet.js watches data-src for lazy loading
      // If lazy=false, use src directly for above-the-fold images
      src={lazy ? undefined : imgSrc}
      data-src={lazy ? imgSrc : undefined}
      data-srcset={lazy && srcSet ? srcSet : undefined}
      srcSet={!lazy && srcSet ? srcSet : undefined}
      alt={alt}
      className={className}
      style={{
        width: width ? `${width}px` : '100%',
        height: height ? `${height}px` : '100%',
        objectFit: fit,
        ...style,
      }}
      loading={lazy ? 'lazy' : 'eager'}
      decoding="async"
      {...rest}
    />
  );
}
